import React, { useState } from 'react';

const Navbar = () => (
  <nav>
    <h1>Food App</h1>
    <ul>
      <li>Home</li>
      <li>About</li>
    </ul>
  </nav>
);

const About = () => (
  <div>
    <h2>About Us</h2>
    <p>
      Welcome to the Food App! We offer a variety of delicious dishes for you to
      choose from.
    </p>
  </div>
);

const Footer = () => (
  <footer>
    <p>&copy; 2024 Food App. All rights reserved.</p>
  </footer>
);

const FoodApp = () => {
  const [foodItems, setFoodItems] = useState([
    { id: 1, name: 'Burger', price: 8.99 },
    { id: 2, name: 'Pizza', price: 10.99 },
    { id: 3, name: 'Salad', price: 5.99 },
  ]);

  const [cart, setCart] = useState([]);

  const addToCart = (foodItem) => {
    setCart([...cart, foodItem]);
  };

  const calculateTotal = () => {
    return cart.reduce((total, item) => total + item.price, 0).toFixed(2);
  };

  return (
    <div className="food-app">
      <Navbar />
      <div className="content">
        <About />
        <div className="menu">
          <h2>Let us found your favourite food</h2>
          <p>Lorem100</p>
          <ul>
            {foodItems.map((item) => (
              <li key={item.id}>
                {item.name} - ${item.price}
                <button onClick={() => addToCart(item)}>Add to Cart</button>
              </li>
            ))}
          </ul>
        </div>
        <div className="cart">
          <h2>Shopping Cart</h2>
          <ul>
            {cart.map((item) => (
              <li key={item.id}>
                {item.name} - ${item.price}
              </li>
            ))}
          </ul>
          <p>Total: ${calculateTotal()}</p>
        </div>
      </div>
      <Footer />
    </div>
  );
};

export default FoodApp;
